(function executeRule(current, previous /* null when async */) {
  const storyPoints = calculateSprintStoryPoints(current.getUniqueValue());
  current.setValue("completed_story_points", storyPoints);

  function calculateSprintStoryPoints(sprintSysId) {
    let sumSPs = 0;
    const sprintGr = new GlideRecord("x_cadso_work_sprint");
    if (sprintGr.get(sprintSysId)) {
      const tasksGr = new GlideRecord("x_cadso_work_task");
      tasksGr.addQuery("sprint", sprintSysId);
      tasksGr.addQuery("state", "40");
      tasksGr.query();
      while (tasksGr.next()) {
        sumSPs += parseFloat(tasksGr.getValue("story_points"));
      }
    }
    return sumSPs;
  }
})(current, previous);

