script.value:
(function executeRule(current, previous /*null when async*/) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic

	var sourceTableGr = new GlideRecord(current.getValue('source_table'));
	if (sourceTableGr.get(current.getValue('sysapproval'))) {
		var comments = current.comments.getJournalEntry(1);
		var indexOf = comments.indexOf("\n");
		var comment = comments.slice(indexOf + 1, comments.length);
		sourceTableGr.setValue('rejection_reason', comment);
		sourceTableGr.update();
	}

})(current, previous);

