(function executeRule(current, previous /*null when async*/) {

	var gr_x_cadso_work_sprint_project_m2m = new GlideRecord('x_cadso_work_sprint_project_m2m');
	gr_x_cadso_work_sprint_project_m2m.addQuery('sprint', current.getUniqueValue());
	gr_x_cadso_work_sprint_project_m2m.query();
	gr_x_cadso_work_sprint_project_m2m.deleteMultiple();

	var workSprintRetroGr = new GlideRecord('x_cadso_work_sprint_retro');
	workSprintRetroGr.addQuery('sprint', current.getUniqueValue());
	workSprintRetroGr.query();
	workSprintRetroGr.deleteMultiple();

	var gr_x_cadso_work_task = new GlideRecord('x_cadso_work_task');
	gr_x_cadso_work_task.addQuery('sprint', current.getUniqueValue());
	gr_x_cadso_work_task.query();

	while (gr_x_cadso_work_task.next()) {
		gr_x_cadso_work_task.setValue('sprint', '');
		gr_x_cadso_work_task.update();
	}

})(current, previous);

