(function executeRule(current, previous /*null when async*/) {

	const project = current.getValue('project');
	const sprint = current.getValue('sprint');
	const index = parseInt(current.getValue('index'));
	const prevIndex = parseInt(previous.getValue('index'));
	const indexIsHigher = index > prevIndex;
	const lowerIndex = indexIsHigher ? prevIndex : index;
	const higherIndex = indexIsHigher ? index : prevIndex;
	const sortByFn = indexIsHigher ? 'orderBy' : 'orderByDesc';

	var sprintProjectM2MGr = new GlideRecord('x_cadso_work_sprint_project_m2m');
	if (sprint) {
		sprintProjectM2MGr.addQuery('sprint', sprint);
	} else {
		sprintProjectM2MGr.addQuery('project', project);
		sprintProjectM2MGr.addEncodedQuery('sprintISEMPTY');
	}
	sprintProjectM2MGr.addQuery('sys_id', '!=', current.getUniqueValue());
	sprintProjectM2MGr.addQuery('index', '>=', lowerIndex);
	sprintProjectM2MGr.addQuery('index', '<=', higherIndex);
	sprintProjectM2MGr[sortByFn]('index');
	sprintProjectM2MGr.query();

	let last = indexIsHigher ? lowerIndex : higherIndex;

	while (sprintProjectM2MGr.next()) {
		const currentIndex = parseInt(sprintProjectM2MGr.getValue('index'));
		sprintProjectM2MGr.setValue('index', last);
		sprintProjectM2MGr.setWorkflow(false);
		sprintProjectM2MGr.update();
		last = currentIndex;
	}

})(current, previous);

