(function executeRule(current, previous /*null when async*/) {

	var gr_x_cadso_work_user = new GlideRecord('x_cadso_work_user');
	gr_x_cadso_work_user.addQuery('user', current.getValue('user'));
	gr_x_cadso_work_user.addQuery('sys_id', '!=', current.getUniqueValue());
	gr_x_cadso_work_user.query();

	if (gr_x_cadso_work_user.next()) {
		gs.addErrorMessage('Only one Work User can exist per user.');
		current.setAbortAction(true);
	}

})(current, previous);

