script.value:
(function executeRule(current, previous /*null when async*/) {

	if (current.campaign.changes() && !previous.campaign.nil()) {
		updateCampaignBudget(previous.campaign.getRefRecord());
	}

	if (!current.campaign.nil()) {
		updateCampaignBudget(current.campaign.getRefRecord());
	}

	// Functions
	function updateCampaignBudget(campaignGr) {
		if (!campaignGr.isValidRecord()) return;
		var budget = getCampaignBudget(campaignGr.getUniqueValue());
		campaignGr.setValue('total_budget', budget);
		campaignGr.update();
	}

	function getCampaignBudget(campaign) {
		var wProjectGr = new GlideRecord('x_cadso_work_project');
		wProjectGr.addQuery('campaign', campaign);
		wProjectGr.query();
		var budget = 0;

		while (wProjectGr.next()) {
			var projectBudget = parseFloat(wProjectGr.getValue('budget'));
			budget += projectBudget;
		}
		return budget;
	}

})(current, previous);

