(function executeRule(current, previous /*null when async*/) {
	const state = current.getValue('state');
	const pState = previous.getValue('state');
	let previousState = current.getValue('previous_state');
	previousState = JSON.parse(previousState);


	if (state == 333) {
		previousState.archive = { status: previous.getValue('status') };
		current.setValue('previous_state', JSON.stringify(previousState));
	} else if (pState != 333) {
		const projectGr = current.project.getRefRecord();
		let wasProjectOnHold = false;
		if (projectGr.isValidRecord()) {
			wasProjectOnHold = projectGr.getValue('state') == 22;
		}
		previousState.onHold = { status: previous.getValue('status'), wasProjectOnHold };
		current.setValue('previous_state', JSON.stringify(previousState));
	}


})(current, previous);

