(function executeRule(current, previous /*null when async*/) {
	var startDate = new GlideDateTime(current.getValue('expected_start'));
	var endDate = new GlideDateTime(current.getValue('expected_end'));
	var schedule = current.getValue('schedule');
	var inheritedSchedule = current.getValue('inherited_schedule') === 'yes';
	if (!schedule || inheritedSchedule) {
		var projectGr = current.project.getRefRecord();
		if (projectGr.isValidRecord()) {
			schedule = projectGr.getValue('schedule');
		}
	}

	if (!schedule) return;

	var scheduleGS = new GlideSchedule();
	scheduleGS.load(schedule);
	var duration = scheduleGS.duration(startDate, endDate);

	current.setValue('duration', duration);

})(current, previous);

