script.value:
(function executeRule(current, previous /*null when async*/) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic
	
    var connectedCampaigns = (current.getValue('campaigns') + '').split(',');

    var campaignId = '';

    for (var i = 0; i < connectedCampaigns.length; i++) {
        campaignId = connectedCampaigns[i].toString();
        
        var campaignGR = new GlideRecord('x_cadso_work_campaign');
        if (campaignGR.get(campaignId)) {
            campaignGR.setValue('goal', current.getUniqueValue());
            campaignGR.update();
        }
    }

})(current, previous);

