(function executeRule(current, previous /*null when async*/) {

	var dollar_amount = parseInt(current.dollar_amount, 10) || 0;
	var dollar_target = parseInt(current.dollar_target, 10) || 0;
	var number_amount = parseInt(current.number_amount, 10) || 0;
	var number_target = parseInt(current.number_target, 10) || 0;
	var percentage_amount = parseInt(current.percentage_amount, 10) || 0;
	var percentage_target = parseInt(current.percentage_target, 10) || 0;

	if (dollar_amount && dollar_target) {
		var progress = (dollar_amount / dollar_target) * 100;
		current.progress = progress;
	} else if (number_amount && number_target) {
		progress = (number_amount / number_target) * 100;
		current.progress = progress;
	} else if (percentage_amount && percentage_target) {
		progress = (percentage_amount / percentage_target) * 100;
		current.progress = progress;
	}
})(current, previous);

