(function executeRule(current, previous /*null when async*/) {
	var taskRelUtil = new x_cadso_work.TaskRelatedUtils();
	let successors = '';

	var template = current.getValue('template');
	var projectTemplate = current.template.changes();
	var previousTemplate = previous.getValue('template');

	if (template) {
		var proTempStepGr = new GlideRecord('x_cadso_work_project_template_step');
		proTempStepGr.addQuery('template', template);
		proTempStepGr.query();

		while(proTempStepGr.next()) {
			successors = taskRelUtil.getSuccessors({ sysId: proTempStepGr.getUniqueValue(), table: 'x_cadso_work_project_template_step' });
			proTempStepGr.setValue('successors', successors);
			proTempStepGr.update();
		}
	}

	if (projectTemplate && previousTemplate) {
		var pProTempStepGr = new GlideRecord('x_cadso_work_project_template_step');
		pProTempStepGr.addQuery('template', previousTemplate);
		pProTempStepGr.query();

		while(pProTempStepGr.next()) {
			successors = taskRelUtil.getSuccessors({ sysId: pProTempStepGr.getUniqueValue(), table: 'x_cadso_work_project_template_step' });
			pProTempStepGr.setValue('successors', successors);
			pProTempStepGr.update();
		}
	}
})(current, previous);

