(function executeRule(current, previous /*null when async*/) {
	var taskRelUtil = new x_cadso_work.TaskRelatedUtils();
	let successors = '';

	var project = current.getValue('project');
	var projectChanges = current.project.changes();
	var previousProject = previous.getValue('project');

	if (project) {
		var taskGr = new GlideRecord('x_cadso_work_task');
		taskGr.addQuery('project', project);
		taskGr.query();

		while(taskGr.next()) {
			successors = taskRelUtil.getSuccessors({ sysId: taskGr.getUniqueValue(), table: 'x_cadso_work_task' });
			taskGr.setValue('successors', successors);
			taskGr.update();
		}
	}

	if (projectChanges && previousProject) {
		var pTaskGr = new GlideRecord('x_cadso_work_task');
		pTaskGr.addQuery('project', previousProject);
		pTaskGr.query();

		while(pTaskGr.next()) {
			successors = taskRelUtil.getSuccessors({ sysId: pTaskGr.getUniqueValue(), table: 'x_cadso_work_task' });
			pTaskGr.setValue('successors', successors);
			pTaskGr.update();
		}
	}

})(current, previous);

