(function executeRule(current, previous /* null when async */) {
  const pastSprint = previous.getValue("sprint");
  const currentSprint = current.getValue("sprint");

  if (pastSprint !== currentSprint && pastSprint) {
    const pastSprintGr = previous.sprint.getRefRecord();
    const pastSp = calculateSprintStoryPoints(pastSprint);
    pastSprintGr.setValue("story_points", pastSp);
    pastSprintGr.update();
  }

  if (currentSprint) {
    const currentSprintGr = current.sprint.getRefRecord();
    const currentSp = calculateSprintStoryPoints(currentSprint);
    currentSprintGr.setValue("story_points", currentSp);
    currentSprintGr.update();
  }

  function calculateSprintStoryPoints(sprintSysId) {
    let sumSPs = 0;
    const sprintGr = new GlideRecord("x_cadso_work_sprint");
    if (sprintGr.get(sprintSysId)) {
      const tasksGr = new GlideRecord("x_cadso_work_task");
      tasksGr.addQuery("sprint", sprintSysId);
      tasksGr.addQuery("state", "NOT IN", "50,60");
      tasksGr.query();

      while (tasksGr.next()) {
        sumSPs += parseFloat(tasksGr.getValue("story_points") || 0);
      }
    }

    return sumSPs;
  }
})(current, previous);

