(function executeRule(current, previous /*null when async*/) {

	if (!current.project.changes() && !current.project.nil()) {
		updateProject(current);
	} else if (!current.project.nil()) {
		updateProject(current);
	} else if (!previous.project.nil()) {
		updateProject(previous);
	}

	function updateProject(tableGr) {
		var workTaskGr = new GlideRecord('x_cadso_work_task');
		workTaskGr.addQuery('project', tableGr.getValue('project'));
		workTaskGr.addEncodedQuery('planned_hoursISNOTEMPTY');
		workTaskGr.query();

		var total = 0;
		while (workTaskGr.next()) {
			total += parseFloat(workTaskGr.getValue('planned_hours'));
		}

		const projectGr = tableGr.project.getRefRecord();
		projectGr.setValue('planned_hours', total);
		projectGr.setWorkflow(false);
		projectGr.update();
	}

})(current, previous);

