(function executeRule(current, previous /*null when async*/) {

	const projectSysId = current.getValue('project');
	const currentSprint = current.getValue('sprint');
	const previousSprint = previous.getValue('sprint');
	let sprints = [];

	const taskGr = new GlideRecord('x_cadso_work_task');
	taskGr.addEncodedQuery('sprintISNOTEMPTY');
	taskGr.addQuery('project', projectSysId);
	taskGr.orderBy('sprint');
	taskGr.query();

	const alreadyAdded = {};
	while (taskGr.next(projectSysId)) {
		const sprint = taskGr.getValue('sprint');
		if (!alreadyAdded[sprint]) {
			alreadyAdded[sprint] = true;
			sprints.push(sprint);
		}
	}

	const projectGr = new GlideRecord('x_cadso_work_project');
	if (projectGr.get(projectSysId)) {
		const oldSprints = projectGr.getValue('sprints');
		sprints = sprints.join();
		if (oldSprints !== sprints) {
			projectGr.setValue('sprints', sprints);
			projectGr.update();
		}
	}

})(current, previous);

