(function executeRule(current, previous /*null when async*/) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic

	if (!current.project.nil()) { 
		updateProjectStoryPoints(current.getValue('project'));
	}

	if (current.project.changes() && !previous.project.nil()) { 
		updateProjectStoryPoints(current.getValue('project'));
	}

	function updateProjectStoryPoints(project) {
		var taskGR = new GlideRecord('x_cadso_work_task');
		taskGR.addQuery('project', project);
		taskGR.query();

		var sumSPs = 0;

		while (taskGR.next()) {
			var taskSP = taskGR.getValue('story_points');
			if (taskSP) {
				sumSPs += parseFloat(taskGR.getValue('story_points'));
			}
		}

		var projGR = new GlideRecord('x_cadso_work_project');
		if (projGR.get(project)) {
			projGR.setValue('story_points', sumSPs);
			projGR.update();
		}
	}

})(current, previous);

