(function executeRule(current, previous /*null when async*/) {
	gs.info("TO - Update Project's Sprints Field (Delete)");
	const project = current.getValue('project');
	const sprint = current.getValue('sprint');


	const projectGr = new GlideRecord('x_cadso_work_project');
	if (projectGr.get(project)) {
		let sprints = projectGr.getValue('sprints') + '';

		if (sprints) {
			sprints = sprints.split(',').filter(_sprint => _sprint != sprint && _sprint !== 'null').join(',');
		}

		if (sprints.length) {
		projectGr.setValue('sprints', sprints);
		projectGr.setWorkflow(false);
		projectGr.update();
		}
	}

	const taskGr = new GlideRecord('x_cadso_work_task');
	taskGr.addQuery('sprint', sprint);
	taskGr.addQuery('project', project);
	taskGr.query();

	while (taskGr.next()) {
		taskGr.setValue('sprint', '');
		taskGr.update();
	}

})(current, previous);

