(function executeRule(current, previous /*null when async*/) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic
	
	if (current.template.nil()) { 
		return;
	}

	var projTempStepGR = new GlideRecord('x_cadso_work_project_template_step');
	projTempStepGR.addQuery('template', current.getValue('template'));
	projTempStepGR.query();

	var sumSPs = 0;

	while (projTempStepGR.next()) {
		var projTempStepSP = projTempStepGR.getValue('story_points');
		if (projTempStepSP) {
			sumSPs += parseFloat(projTempStepGR.getValue('story_points'));
		}
	}

	var projTemplateGR = new GlideRecord('x_cadso_work_project_template');
	if (projTemplateGR.get(current.getValue('template'))) {
		projTemplateGR.setValue('story_points', sumSPs);
		projTemplateGR.update();
	}

})(current, previous);

