(function executeRule(current, previous /*null when async*/) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic

	// If current.project_template is not nil()
	// Update current project templates with the right count
	// If previous.project_template is not nil()
	// Update previous project templates with the right count

	if (!current.project_template.nil()) {
		var projectGA = new GlideAggregate('x_cadso_work_project');
		projectGA.addQuery('project_template', current.getValue('project_template'));
		projectGA.addAggregate('COUNT');
		projectGA.query();

		if (projectGA.next()) {
			var projTempGR = new GlideRecord('x_cadso_work_project_template');
			if (projTempGR.get(current.getValue('project_template'))){
				var numberOfProjects = projectGA.getAggregate('COUNT');
				projTempGR.setValue('number_of_projects', numberOfProjects);
				projTempGR.update();
			}
		}
	} else if (!previous.project_template.nil()) {
		var originalProjGA = new GlideAggregate('x_cadso_work_project');
		originalProjGA.addQuery('project_template', previous.getValue('project_template'));
		originalProjGA.addAggregate('COUNT');
		originalProjGA.query();

		if (originalProjGA.next()) {
			var prevProjTempGR = new GlideRecord('x_cadso_work_project_template');
			if (prevProjTempGR.get(previous.getValue('project_template'))){
				var numberOfProjects2 = originalProjGA.getAggregate('COUNT');
				prevProjTempGR.setValue('number_of_projects', numberOfProjects2);
				prevProjTempGR.update();
			}
		} else {
			var prevProjTempGR2 = new GlideRecord('x_cadso_work_project_template');
			if (prevProjTempGR2.get(previous.getValue('project_template'))){
				prevProjTempGR2.setValue('number_of_projects', 0);
				prevProjTempGR2.update();
			}
		}
	}


})(current, previous);

