(function executeRule(current, previous /*null when async*/) {

	var projectTemplateStepGr = new GlideRecord('x_cadso_work_project_template_step');
	projectTemplateStepGr.addQuery('template', current.getUniqueValue());
	projectTemplateStepGr.query();

	var transferScheduleChanges = current.transfer_schedule.changes();
	var transferSchedule = current.getValue('transfer_schedule') === 'yes';
	var scheduleChanges = current.schedule.changes();

	while (projectTemplateStepGr.next()) {
		if (scheduleChanges && transferSchedule) {
			projectTemplateStepGr.setValue('schedule', current.getValue('schedule'));
		} else if (transferScheduleChanges && transferSchedule) {
			projectTemplateStepGr.setValue('schedule', current.getValue('schedule'));
		}

		if (transferScheduleChanges) {
			projectTemplateStepGr.setValue('inherited_schedule', current.getValue('transfer_schedule'));
		}

		projectTemplateStepGr.update();
	}

})(current, previous);

