(function executeRule(current, previous /*null when async*/ ) {
    //lookup all templates using the selected type, validate the current record isn't included in the lookup to allow updates to the record
    var typeUsedGr = new GlideRecord('x_cadso_work_project_template');
	typeUsedGr.addQuery('type', current.getValue('type'));
	typeUsedGr.addQuery('sys_id', '!=', current.getUniqueValue());
    typeUsedGr.query();
    if (typeUsedGr.next()) {
        current.setAbortAction(true);
        gs.addErrorMessage("A template is already associated with this Project Type. Project Types and Project Templates mush have a 1 to 1 relationship. Please select a new type or update/retire the existing template.");
    }
})(current, previous);

