function onLoad() {
   //Type appropriate comment here, and begin script below

   var projectValue = g_form.getValue('project');
   var ga = new GlideAjax('MarketingTaskTypeValidations');
    ga.addParam('sysparm_name', 'isAdhocSet');
    ga.addParam('sysparm_project', projectValue);
    ga.getXMLAnswer(MarketingAdhocTaskValidations);
	
	function MarketingAdhocTaskValidations(answer) {
        // alert('Project has adhoc? ' + answer+" "+typeof g_form.getValue('ad_hoc'));
        if (answer == 'true') {
            g_form.setValue('ad_hoc', "true");
        } else {
            g_form.setValue('ad_hoc', "false");
        }

    }
   
}

