function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('MarketingTaskTypeValidations');
    ga.addParam('sysparm_name', 'isAdhocSet');
    ga.addParam('sysparm_project', newValue);
    ga.getXMLAnswer(processMarketingTaskValidations);


    function processMarketingTaskValidations(answer) {
        // alert('Project has adhoc? ' + answer+" "+typeof g_form.getValue('ad_hoc'));
        if (answer == 'true') {
            //Display false and un-mandatory
            g_form.setValue('calculate_dates', 'adHoc');
            g_form.setValue('ad_hoc', "true");

            // g_form.setVisible('successors',false);
            // g_form.setVisible('dependencies',false);
            // g_form.setVisible('schedule',false);
            // g_form.setVisible('inherited_schedule',false);
            // g_form.setMandatory('duration',false);
            // g_form.setMandatory('schedule',false);
            // g_form.setVisible('duration',false);
        } else {
            g_form.setValue('calculate_dates', 'start');
            g_form.setValue('ad_hoc', "false");

            // g_form.setVisible('successors',true);
            // g_form.setVisible('dependencies',true);
            // g_form.setVisible('duration',true);
            // g_form.setVisible('schedule',true);
            // g_form.setVisible('inherited_schedule',true);
            // g_form.setMandatory('duration',true);
            // g_form.setMandatory('schedule',true);
        }

    }





}

