/* eslint-disable no-var */
function onChange(control, oldValue, newValue, isLoading, isTemplate) {	
	if (isLoading) return;
	if (newValue && newValue != oldValue) {
		getRef()
	} else if (!newValue) {
		g_form.setReadOnly('inherited_schedule', false);
		g_form.setValue('schedule', '');
		g_form.setValue('inherited_schedule', 'no');
	}

	function getRef() {
		var ga = new GlideAjax('x_cadso_work.WorkClientUtils');
		ga.addParam("sysparm_name", "getRefRec");
		ga.addParam("sysparm_table", 'x_cadso_work_project');
		ga.addParam("sysparm_sys_id", newValue);
		ga.addParam("sysparm_fields", 'schedule,transfer_schedule');
		ga.getXMLAnswer(getResponse);

		function getResponse(answer) {
			answer = JSON.parse(answer);
			var transferSchedule = answer.transfer_schedule.value;
			var schedule = answer.schedule.value;
			g_form.setValue('inherited_schedule', transferSchedule);
			if (transferSchedule === 'yes') {
				g_form.setValue('schedule', schedule);
				g_form.setReadOnly('inherited_schedule', true);
			} else if (g_form.getValue('assignment_group')) {
				g_form.setReadOnly('inherited_schedule', false);
				getSchedule(g_form.getValue('assignment_group'));
			} else {
				g_form.setReadOnly('inherited_schedule', false);
			}
		}
	}
}

function getSchedule(assignmentGroup) {
	var ga = new GlideAjax('x_cadso_work.ScheduleClientUtils');
	ga.addParam("sysparm_name", "getSchedule");
	ga.addParam("sysparm_group", assignmentGroup);
	ga.getXMLAnswer(getResponse);

	function getResponse(answer) {
		g_form.setValue('schedule', answer);
	}
}

