function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	var isNewRecord = g_form.isNewRecord();
	if (!isNewRecord) return;
	if (!newValue) {
		g_form.setValue('order', '');
	} else if (newValue) {
		var ga = new GlideAjax('x_cadso_work.WorkClientUtils');
		ga.addParam("sysparm_name", "increaseIndex");
		ga.addParam("sysparm_table", 'x_cadso_work_project_template_step');
		ga.addParam("sysparm_query_value", newValue);
		ga.addParam("sysparm_query_field", 'template');
		ga.addParam("sysparm_index_field", "order");
		ga.addParam("sysparm_amount", 100);
		ga.getXMLAnswer(getResponse);
	}

	function getResponse(answer) {
		answer = (answer || 100) + '';
		answer = answer.split('.')[0];
		g_form.setValue('order', answer);
	}
}

