function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        if (g_form.getValue('calculate_dates').indexOf('start') > -1) {
            if (g_form.getValue('ad_hoc') == 'true') {
                g_form.setMandatory('expected_start', false);

            } else {
                g_form.setMandatory('expected_start', true);
            }
            //g_form.setMandatory('expected_start', true);
           // g_form.setMandatory('expected_end', false);
            g_form.setVisible('expected_start', true);
            g_form.setVisible('expected_end', true);
            g_form.setReadOnly('expected_start', false);
            g_form.setReadOnly('expected_end', true);

        } else {
            g_form.setMandatory('expected_start', false);
            //g_form.setMandatory('expected_end', true);
            // g_form.setVisible('expected_start', false);
            g_form.setVisible('expected_start', true);
            g_form.setVisible('expected_end', true);
            g_form.setReadOnly('expected_start', true);
            g_form.setReadOnly('expected_end', false);
        }
        return;
    }
    if (newValue) {
        getType(newValue);
    } else {
        nothingFound();
    }

    function getType(newValue) {
        var ga = new GlideAjax('x_cadso_work.WorkClientUtilsMS');
        ga.addParam("sysparm_name", "getCalcDates");
        ga.addParam("sysparm_type", newValue);
        ga.getXMLAnswer(getResponse);

        function getResponse(answer) {
            if (answer) {
                if (answer.indexOf('start') > -1) {
                    if (g_form.getValue('ad_hoc') == 'true') {
                        g_form.setMandatory('expected_start', false);

                    } else {
                        g_form.setMandatory('expected_start', true);
                    }
                    //g_form.setMandatory('expected_start', true);	
                    g_form.setMandatory('expected_end', false);
                    g_form.setVisible('expected_start', true);
                    g_form.setVisible('expected_end', false);
                    g_form.setReadOnly('expected_start', false);
                    g_form.setReadOnly('expected_end', true);
                } else {

                    g_form.setMandatory('expected_start', false);
                    g_form.setMandatory('expected_end', true);
                    g_form.setVisible('expected_start', false);
                    g_form.setVisible('expected_end', true);
                    g_form.setReadOnly('expected_start', false);
                    g_form.setReadOnly('expected_end', false);
                }

                g_form.setReadOnly('calculate_dates', true);
                g_form.setValue('calculate_dates', answer);
                g_form.setMandatory('calculate_dates', false);
                g_form.setReadOnly('transfer_schedule', true);
                g_form.setMandatory('transfer_schedule', false);
                g_form.setReadOnly('schedule', true);
                g_form.setMandatory('schedule', false);
            } else {
                nothingFound();
            }
        }
    }

    function nothingFound() {
        if (g_form.getValue('ad_hoc') == 'true') {
            g_form.setMandatory('expected_start', false);

        } else {
            g_form.setMandatory('expected_start', true);
        }
        //g_form.setMandatory('expected_start', true);
        g_form.setMandatory('expected_end', false);
        g_form.setVisible('expected_start', true);
        g_form.setVisible('expected_end', false);
        g_form.setReadOnly('calculate_dates', false);
        g_form.setReadOnly('transfer_schedule', false);
        g_form.setReadOnly('schedule', false);
    }
}

