script.value:
/* eslint-disable no-var */
function onChange(control, oldValue, newValue, isLoading, isTemplate) {	
	var isApproval = g_form.getValue('type') !== 'task';

	if (isApproval) {
		g_form.setVisible("segment", false);
	} else if (newValue) {
		getRef();
	}  else {
		g_form.setVisible("segment", true);
	}



	function getRef() {
		var ga = new GlideAjax('x_cadso_work.WorkClientUtils');
		ga.addParam("sysparm_name", "getRefRec");
		ga.addParam("sysparm_table", 'x_cadso_work_project_template');
		ga.addParam("sysparm_sys_id", newValue);
		ga.addParam("sysparm_fields", 'copy_segment');
		ga.getXMLAnswer(getResponse);

		function getResponse(answer) {
			answer = JSON.parse(answer);
			var copy = answer.copy_segment.value;
			copy = copy === "true" || copy === "1" || copy === true;
			if (copy) {
				g_form.setVisible("segment", false);
			} else {
				g_form.setVisible("segment", true);
			}
		}
	}
}

