var ContactDetailUtilMS = Class.create();
ContactDetailUtilMS.prototype = {
    initialize: function() {
    },

	getContactDetailFromEmailAddress: function(emailAddress){
		if(!emailAddress){
			return null;
		}
		let emailGQ = new global.GlideQuery('x_cadso_core_email_address')
		.getBy({ email: emailAddress }, ['sys_id', 'contact_detail'])
		.orElse(null);
		return emailGQ;
	},

	getContactDetailFromPhoneNumber: function(phoneNumber){
		if(!phoneNumber){
			return null;
		}
		let phoneGQ = new global.GlideQuery('x_cadso_core_phone_number')
		.getBy({ phone_number: phone_number }, ['sys_id', 'contact_detail'])
		.orElse(null);
		return phoneGQ;
	},

	createContactDetail: function({ contact, lead, user, consumer, account }){
		let contactDetail = {};
		if(contact){
			contactDetail.contact = contact;
		}
		if(user){
			contactDetail.user = user;
		}if(lead){
			contactDetail.lead = lead;
		}if(consumer){
			contactDetail.consumer = consumer;
		}if(account){
			contactDetail.account = account;
		}
		let contactDetailGQ = new global.GlideQuery('x_cadso_automate_contact_detail')
		.insert(contactDetail)
		.get();
		return contactDetailGQ;
	},



    type: 'ContactDetailUtilMS'
};

