const CoreSettingsMS = Class.create();
CoreSettingsMS.prototype = {
    initialize: function() {

    },

    get: function(name) {
        var coreSettingsGr = new GlideRecordSecure('x_cadso_core_setting');
		coreSettingsGr.addQuery('name', name);
		coreSettingsGr.addActiveQuery();
		coreSettingsGr.setLimit(1);
		coreSettingsGr.orderByDesc('sys_updated_on');
        coreSettingsGr.query();
		let value = undefined;
        while (coreSettingsGr.next()) {
			value = coreSettingsGr.getValue('value') + '';
        }
		return value;
    },

    type: "CoreSettingsMS",
};

