var CreateDKIMKeyMS = Class.create();
CreateDKIMKeyMS.prototype = {
    initialize: function() {},

    triggerFlow: function({
        domainSysId,
        selector,
        bits
    }) {
        try {
            var inputs = {};
            inputs['domain_sys_id'] = domainSysId; // String 
            inputs['selector'] = selector; // String 
            inputs['bits'] = bits; // Choice 

            // Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
            // sn_fd.FlowAPI.getRunner().subflow('x_cadso_automate.create_a_new_dkim').inBackground().withInputs(inputs).run();

            // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
            var result = sn_fd.FlowAPI.getRunner().subflow('x_cadso_automate.create_a_new_dkim').inForeground().withInputs(inputs).run();
            var outputs = result.getOutputs();

            // Current subflow has no outputs defined.		
        } catch (ex) {
            var message = ex.getMessage();
            gs.error(message);
        }
    },

    type: 'CreateDKIMKeyMS'
};

