var HMACMessageBase64 = Class.create();
HMACMessageBase64.prototype = {
    initialize: function(request, response, hmacAuthUtil, credential) {
      this.request = request;
      this.response = response;
      this.hmacAuthUtil = hmacAuthUtil;
      this.credential = credential;
    },

	buildHMACInfo: function() {
		var keyId = 'x_cadso_automate.Tenon_Message_Webhook';
		var payload = this.hmacAuthUtil.getBodyAsString();
		var signature = this.request.getHeader('x-sinch-webhook-signature');
		var nonce = this.request.getHeader('x-sinch-webhook-signature-nonce');
		var timestamp = this.request.getHeader('x-sinch-webhook-signature-timestamp');
		var signedData = payload + '.' + nonce + '.' + timestamp;

		if (keyId)
			this.hmacAuthUtil.setKeyId(keyId);
		if (signature)
			this.hmacAuthUtil.setSignature(signature);
		if (signedData)
			this.hmacAuthUtil.setContentToSign(signedData);
		this.hmacAuthUtil.setEncoding('Base64');
	},

    type: 'MessageHMACInternalBase64'
};

