var VerifyDNSSettingsMS = Class.create();
VerifyDNSSettingsMS.prototype = {
    initialize: function() {
    },

    triggerVerification: function({ domain }) {
		gs.info("DM dns verification {0}", domain);
        var domainGr = new GlideRecord('x_cadso_automate_domain');

        if (domainGr.get(domain)) {
			domainGr.setValue('verification_status', 'verifying');
			return !!domainGr.update();
        }

        return false;
    },

    type: 'VerifyDNSSettingsMS'
};

