const CustomFilterApiMS = Class.create();
CustomFilterApiMS.prototype = {
	initialize: function () {
	},


	get_references: function (request, response) 
	{
		let { filterTable = '', fields = [], queryString = '' } = request.body.data;
		var results = [];

		for(var x = 0; x < fields.length; x++)
		{
			const field = new x_cadso_work.GetTableDropdownsData().updateReferenceFieldOptions(filterTable, fields[x], queryString);
			results.push(field);
		}

		response.setBody({ results });
		return response;
	},
	type: "CustomFilterApiMS",
};

