var FeatureAvailability = Class.create();
FeatureAvailability.prototype = {
    initialize: function() {
    },

	isLeadEnabled: function() {
		let leadDisableProp = gs.getProperty('x_cadso_work.disable_lead_features');
		if (leadDisableProp === 'true') {
			return false;
		} else {
			let pMgr = new GlidePluginManager();
			return pMgr.isActive('sn_lead_mgmt_core');
		}
	},

	isContactEnabled: function() {
		let contactDisableProp = gs.getProperty('x_cadso_work.disable_contact_features');
		if (contactDisableProp === 'true') {
			return false;
		} else {
			let pMgr = new GlidePluginManager();
			return pMgr.isActive('com.sn_customerservice');
		}
	},

	isConsumerEnabled: function() {
		let consumerDisableProp = gs.getProperty('x_cadso_work.disable_consumer_features');
		if (consumerDisableProp === 'true') {
			return false;
		} else {
			let pMgr = new GlidePluginManager();
			return pMgr.isActive('com.sn_customerservice');
		}
	},

		isAccountEnabled: function() {
		let accountDisableProp = gs.getProperty('x_cadso_work.disable_account_features');
		if (accountDisableProp === 'true') {
			return false;
		} else {
			let pMgr = new GlidePluginManager();
			return pMgr.isActive('com.sn_customerservice');
		}
	},

    type: 'FeatureAvailability'
};

