var FetchFormListFieldsMS = Class.create();
FetchFormListFieldsMS.prototype = {
    initialize: function() {
    },

	fetch: function({
        table,
		query,
		orderByField = "name",
		orderByDirection = "ASC",
		options
    }) {
		if (!table) {
            throw new Error('Must provide a table');
        }

		if (!query) {
            throw new Error('Must provide a query');
        }
		
		var result = {
			items: []
		};
		
		// Get glide record from table
		var gr = new GlideRecord(table);
		gr.addEncodedQuery(query);
		
		if (orderByField) {
			if (orderByDirection === 'DESC') {
				gr.orderByDesc(orderByField);
			} else {
				gr.orderBy(orderByField);
			}
		} else {
			gr.orderBy(labelField);
		}
		gr.query();
		while (gr.next()) {
			var obj = {};
			obj.utm = gr.getValue("utm");
			obj.addOnText = gr.getValue("utm");	
			obj.value = gr.getValue("value");
			obj.name = gr.getValue("name");
			obj.disabled = gr.getValue(disabled);
			obj.helperText = gr.getValue('parameter');

			result.items.push(obj);
			
			return result;
		}
	},

    type: 'FetchFormListFieldsMS'
};

