        let actions = this.getTableActions(table);
        var statusField = table === 'x_cadso_work_task' ? 'status' : 'state';

        const tableGr = new GlideRecordSecure(table);
        const tableMeta = {
            canWrite: tableGr.canWrite(),
            canRead: tableGr.canRead(),
            canCreate: tableGr.canCreate(),
            canDelete: tableGr.canDelete(),
            canUpdateStatus: false
        };

        try {
            tableMeta.canUpdateStatus = tableGr[statusField].canWrite();
        } catch (e) {
            // Do nothing. Above is a fail safe for non task fields
        }

        const requireAclCheck = (this.enforceWriteACL && tableMeta.canWrite);
        actions = actions.map(function(action = {
