                    details = e;
                    error = true;
                }

                fieldData.meta = {
                    ...meta,
                    error,
                    errorMessage,
                    details
                };
            }

            return {
                ...action,
                ...fieldData
            };
        }, this);

        return {
            actions: [...actions, ...this.getUtilityActions({
                table,
                tableMeta
            })],
            tableMeta
        };
    },

