var MarketingTaskTypeValidations = Class.create();
MarketingTaskTypeValidations.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    isAdhocSet: function() {
        //x_cadso_work_task
        var prj = new GlideRecordSecure('x_cadso_work_project');
        if (prj.get(this.getParameter('sysparm_project')) && prj.getValue('ad_hoc') == "1") {
            return "true";
        } else {
            return "false";
        }

    },

    type: 'MarketingTaskTypeValidations'
});

