var ProjectTemplateValidation = Class.create();
ProjectTemplateValidation.prototype = {
    initialize: function() {},
    validateProjectCategory: function() {
        gs.info('In Script Include');
        var projectCategories = [];
        var prG = new GlideRecord('x_cadso_work_project_category');
        prG.addEncodedQuery('active=true');
        prG.query();
        while (prG.next()) {
            var prT = new GlideRecord('x_cadso_work_project_type');
            prT.addQuery('category', prG.getUniqueValue());
            prT.query();
            if (prT.next()) {
                projectCategories.push(prG.getUniqueValue());
            }
        }
        gs.info("Category List:" + projectCategories);
        return 'sys_idIN' + projectCategories.toString();

    },

    type: 'ProjectTemplateValidation'
};

