var RefQualApiMS = Class.create();
RefQualApiMS.prototype = {
	initialize: function() {
	},

	getProjectTypes: function(category, type) {
		const workProjectTemplateGr = new GlideRecordSecure('x_cadso_work_project_template');
		workProjectTemplateGr.addQuery('category', category);
		workProjectTemplateGr.addActiveQuery();
		workProjectTemplateGr.query();
		const activeCategoryTypes = [];

		while (workProjectTemplateGr.next()) {
			const _type = workProjectTemplateGr.getValue('type');
			if (type === _type) continue;
			activeCategoryTypes.push(_type);
		}

		var workProjectTypeGr = new GlideRecordSecure('x_cadso_work_project_type');
		activeCategoryTypes.forEach((sysId) => {
			workProjectTypeGr.addQuery('sys_id', '!=', sysId);
		});
		workProjectTypeGr.addQuery('category', category);
		workProjectTypeGr.addActiveQuery();
		workProjectTypeGr.query();

		const availableTypes = [];
		while (workProjectTypeGr.next()) {
			availableTypes.push(workProjectTypeGr.getUniqueValue());
		}

		return availableTypes.length ? 'sys_idIN' + availableTypes.join(',') : 'sys_id=null';
	},

	type: 'RefQualApiMS'
};

