var TaskRelatedUtilsMS = Class.create();
TaskRelatedUtilsMS.prototype = {
	initialize: function() {
	},

	getSuccessors: function({sysId, table}) {
		var sysIds = [];

		var workTaskGr = new GlideRecord(table);
		workTaskGr.addQuery('dependencies', 'CONTAINS', sysId);
		workTaskGr.query();

		while (workTaskGr.next()) {
			sysIds.push(workTaskGr.getUniqueValue());
		}

		return sysIds.join(',');
	},

	type: 'TaskRelatedUtilsMS'
};

