var ValidationMS = Class.create();
ValidationMS.prototype = {
    initialize: function() {},

    Validate: function(name, value) {
        const validategr = new GlideRecordSecure('x_cadso_work_property'); //Property Table
        validategr.addQuery('name', name); //Scope name field on the table
        validategr.addQuery('value', value); //Value field on the table
        validategr.query();

        if (validategr.next()) {
            return true; // Record exists and has a value
       } else {
            return false; // Record does not exist
        }
    },

    type: 'ValidationMS'
};

