var WorkReferenceQualifierMS = Class.create();
WorkReferenceQualifierMS.prototype = {
	initialize: function() {},

	partOfGroup: function(group, table) {
		if(group) {
			var usersList = [];
			var GR = new GlideRecord(table);
			GR.addEncodedQuery('group=' + group);
			GR.query();

			while(GR.next()) {
				usersList.push(GR.getValue('user'));
			}

			return 'sys_idIN' + usersList.length ? usersList.join() : '';
		} else {
			return;
		}
	},

	type: 'WorkReferenceQualifierMS'
};

