var WorkStatusMS = Class.create();
WorkStatusMS.prototype = {
	initialize: function({ table, state, status }) {
		this.table = table;
		this.state = state || status;
	},

	fetchStatus: function() {
		var workStatusGr = new GlideRecord('x_cadso_work_status');
		workStatusGr.addQuery('state', (this.state + ''));
		workStatusGr.addQuery('table', this.table);
		workStatusGr.addQuery('active', true);
		workStatusGr.orderBy('sequence');
		workStatusGr.setLimit(1);
		workStatusGr.query();
		let status = '';

		if (workStatusGr.next()) {
			status = workStatusGr.getUniqueValue();
		}

		return status;
	},

	type: 'WorkStatusMS'
};

