var denyApprovalApiMS = Class.create();
denyApprovalApiMS.prototype = {
	initialize: function() {
	},

	denyApprovalREST: function(request, response) {
		const { sysId, comment } = request.body;
		return this.denyApproval(sysId, comment);
	},

	denyApproval: function(sysId, comment) {
		let data = { success: false, message: 'Approval has NOT been rejected' };
		
		if (gs.nil(sysId)){
			data.message = 'Record not found.';
			return data;
		}
		if (gs.nil(comment)){
			data.message = 'Requires a comment.';
			return data;
		}

		const approvalGr = new GlideRecordSecure('sysapproval_approver');

		if (approvalGr.get(sysId)) {
			approvalGr.comments = comment;
			approvalGr.setValue('state', 'rejected');
			approvalGr.update();
			data = { success: true, message: 'Approval has been rejected' };
		}

		return data;
	},

	type: 'denyApprovalApiMS'
};

