var getSmsImageMS = Class.create();
getSmsImageMS.prototype = {
    initialize: function() {

    },

    getImage: function(table, record) {

		let returnArr = [];
        if (table && record) {
			let index = 0;
            const attachment = new GlideSysAttachment();
            const agr = attachment.getAttachments(table, record);

            while (agr.next()) {
                const contentType = agr.getValue('content_type');
                if (contentType.startsWith('image')) {
                    const imageData = attachment.getContentBase64(agr);
                    const label = agr.getValue('file_name');
					const value = "data:" + contentType + ';base64,' + imageData;
                    returnArr.push( {
                        label,
                        value,
						index,
                        type: "attachement"
                    });
                }
				index++;
            }
        }


        return returnArr;
    },

    type: 'getSmsImageMS'
};

