var randomizeGlyphColor = Class.create();
randomizeGlyphColor.prototype = {
	initialize: function() {
	},

	getLinearColor: function(table) {
		var startColorIndex = 0;
		var colorList = ["#FE853B","#FC8DFE","#99A4FF","#A5E4FF","#FE6666","#77F059","#32B57F","#FEED51","#A1C62C","#AC480B","#B229B5","#3141C6","#3CA0CB","#00663C","#35991C","#BEAC02","#CCF352","#B22727","#987E59","#24402F","#6A8173"];

		var tableName = current.getTableName();
		var fn = 'getRow' + 'Count';
		var campaignGr = new GlideRecordSecure('x_cadso_work_campaign');
		campaignGr.query();
		var nextColorIndex = startColorIndex + campaignGr[fn]() - 1;

		var nextColor = colorList[nextColorIndex % colorList.length];
		return nextColor;
	},

	getRandomColor: function(tbl, sysId) {
		var tblGR = new GlideRecordSecure(tbl);
		tblGR.query();
		var res = '';

		if (tblGR.next()) {
			var choices = tblGR.glyph_color.getChoices();
			res = choices[Math.floor(Math.random() * choices.length)];
		}
		return res;
	},

	type: 'randomizeGlyphColor'
};

