(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        const apiUtil = new x_cadso_automate.APIUtils();
        const table = 'x_cadso_core_option';
        const optionGR = new GlideRecord(table);

        if (!optionGR.get(request.pathParams.sysID)) {
            return new sn_ws_err.NotFoundError('Could Not Find Option');
        }

        const sysID = request.pathParams.sysID;
        const optionDeleted = apiUtil.delete({
            table,
            sysID
        });

        if (!optionDeleted) {
            return new sn_ws_err.NotFoundError('Could not delete Journey');
        }

        response.setStatus(204);
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }
})(request, response);

