(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    const {
        sysId,
        table
    } = request.pathParams;

    const tableGr = new GlideRecord(table);
    if (tableGr.get(sysId)) {
		if (table === 'x_cadso_cloud_page') {
			response.setBody({
				html: tableGr.getValue('html'),
				thumbnail: tableGr.getDisplayValue('thumbnail'),
				thumbnailLarge: tableGr.getDisplayValue('thumbnail_large')
			});
		} else if (table.indexOf('email') > -1) {
			response.setBody({
				profile: tableGr.getDisplayValue('from_address'),
				dynamicSubjectLine: tableGr.getDisplayValue('dynamic_subject_line'),
				subjectLine: tableGr.getDisplayValue('static_subject_line'),
				previewText: tableGr.getDisplayValue('preview_text'),
				html: tableGr.getValue('email_html'),
				thumbnail: tableGr.getDisplayValue('thumbnail'),
				thumbnailLarge: tableGr.getDisplayValue('thumbnail_large')
			});
		} else {
			response.setBody({
				richText: tableGr.getDisplayValue('rich_text_object'),
			});
		}
    }

	return response;

})(request, response);

