(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	try {
		var body = request.body.data;

		// Checking for required fields
		var required = ['name', 'record_table'];
		for (var i = 0; i < required.length; i++) {
			if (!body[required[i]]) {
				response.setStatus(400);
				return { error: `Missing required field: ${required[i]}` };
			}
		}

		var gr = new GlideRecord('x_cadso_form_form');
		gr.initialize();

		gr.name = body.name;
		gr.slug = body.slug;
		gr.record_table = body.record_table;
		gr.owner = gs.getUserID();

		if (body.description) gr.description = body.description;
		if (body.form_data) gr.form_data = JSON.stringify(body.form_data);
		if (body.publish_date) gr.publish_date = body.publish_date;
		if (body.status) gr.status = body.status;
		if (body.subdomain) gr.subdomain = body.subdomain;
		if (body.thumbnail) gr.thumbnail = body.thumbnail;
		if (body.thumbnail_large) gr.thumbnail_large = body.thumbnail_large;

		var sys_id = gr.insert();

		response.setStatus(201);
		return { message: "Form created successfully", sys_id: sys_id };

	} catch (e) {
		gs.error("Form API Error: " + e.message);
		response.setStatus(500);
		return { error: "Internal Server Error", details: e.message };
	}
})(request, response);

